import { useDispatch, useSelector } from "react-redux";
import { toggleMenu } from "../utils/appSlice";
import { useEffect, useState } from "react";
import { YOUTUBE_SRARCH_API } from "../utils/contants";
import { cacheResults } from "../utils/searchSlice";
import { Link } from "react-router-dom";

const Head = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  const searchCache = useSelector((store) => store.search);

  const dispatch = useDispatch();
  useEffect(() => {
    const timer = setTimeout(() => {
      if (searchCache[searchQuery]) {
        setSuggestions(searchCache[searchQuery]);
      } else {
        getSearchSuggestions();
      }
    }, 200);
    return () => {
      clearTimeout(timer);
    };
  }, [searchQuery]);

  const getSearchSuggestions = async () => {
    const data = await fetch(YOUTUBE_SRARCH_API + searchQuery);
    const json = await data.json();
    setSuggestions(json[1]);

    // update cache
    dispatch(
      cacheResults({
        [searchQuery]: json[1],
      })
    );
  };
  const toggleMenuHandler = () => {
    dispatch(toggleMenu());
  };

  const submitHandler = (e) => {
    e.preventDefault();
  };

  return (
    <div className="grid grid-flow-col p-2 m-2 shadow-lg">
      <div className="flex col-span-1">
        <img
          onClick={() => toggleMenuHandler()}
          className="h-6 cursor-pointer"
          src="data:image/png;base64,"
          alt="hamburger menu"
        />
        <Link to={"/"}>
          <img
            className="h-6 mx-4"
            src="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b8/YouTube_Logo_2017.svg/1024px-YouTube_Logo_2017.svg.png"
            alt="youtube logo"
          />
        </Link>
      </div>

      <form className="col-span-10 px-40" onSubmit={submitHandler}>
        <div>
          <input
            className="w-8/12 border border-gray-400 p-2 rounded-l-full"
            type="text"
            placeholder="Search"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onFocus={() => setShowSuggestions(true)}
            onBlur={() => setShowSuggestions(false)}
          />
          <Link to={`/search?q= ${searchQuery}`}>
            <button className="border border-gray-400 p-2 rounded-r-full bg-gray-100">
              🔍
            </button>
          </Link>
        </div>
        {showSuggestions && (
          <div className="fixed bg-white py-2  w-[26.8rem] shadow-lg rounded-lg border border-gray-200">
            <ul>
              {suggestions?.map((s) => (
                <li key={s} className="hover:bg-gray-200 px-5">
                  {s}
                </li>
              ))}
            </ul>
          </div>
        )}
      </form>

      <div className="col-span-1">
        <img
          className="h-6"
          src="https://www.iconpacks.net/icons/2/free-user-icon-3296-thumb.png"
          alt="user icon"
        />
      </div>
    </div>
  );
};

export default Head;
